import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import axios from 'axios';
import './AppointmentPage.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const localizer = momentLocalizer(moment);

const AppointmentPage = () => {
    const [events, setEvents] = useState([]);
    const [selectedDate, setSelectedDate] = useState(null);
    const [selectedTime, setSelectedTime] = useState(null);
    const [formVisible, setFormVisible] = useState(false);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        contactNo: '',
    });

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/appointments');
            const appointments = response.data.map(appointment => ({
                title: 'Booked',
                start: new Date(appointment.date),
                end: new Date(new Date(appointment.date).getTime() + 30 * 60000), // Assuming 30 minute slots
                allDay: false,
            }));
            setEvents(appointments);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    const handleSelectSlot = ({ start }) => {
        const isSlotBooked = events.some(event =>
            start >= event.start && start < event.end
        );

        const isWithinTimeSlot = (
            (start.getHours() >= 10 && start.getHours() < 13) ||
            (start.getHours() >= 15 && start.getHours() < 20)
        );

        const isFutureDate = moment(start).isAfter(moment().endOf('day'));

        if (!isFutureDate) {
            toast.error('You can only book appointments from tomorrow onwards.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else if (!isWithinTimeSlot) {
            toast.error('Appointments can only be booked between 10 AM - 1 PM and 3 PM - 8 PM.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else if (isSlotBooked) {
            toast.error('This slot is already booked. Please choose another one.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else {
            setSelectedDate(start);
            setFormVisible(true);
        }
    };

    const handleTimeSlotClick = (time) => {
        setSelectedTime(time);
    };

    const generateTimeSlots = (date) => {
        const timeslots = [];
        const startTimes = [10, 15];
        const endTimes = [13, 20];
        startTimes.forEach((start, index) => {
            for (let hour = start; hour < endTimes[index]; hour++) {
                for (let minute = 0; minute < 60; minute += 30) {
                    const time = new Date(date);
                    time.setHours(hour, minute, 0, 0);
                    const isSlotBooked = events.some(event =>
                        time >= event.start && time < event.end
                    );
                    timeslots.push({
                        time,
                        isBooked: isSlotBooked
                    });
                }
            }
        });
        return timeslots;
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedTime) return;

        const { name, email, contactNo } = formData;

        if (name && email && contactNo) {
            try {
                const response = await axios.post('http://localhost:5000/api/appointments', {
                    name,
                    email,
                    contactNo,
                    date: selectedTime,
                });
                if (response.data) {
                    toast.success('Appointment booked successfully!', {
                        position: 'top-right',
                        autoClose: 5000,
                    });
                    fetchAppointments();
                    setSelectedDate(null);
                    setSelectedTime(null);
                    setFormVisible(false);
                    setFormData({ name: '', email: '', contactNo: '' });
                }
            } catch (error) {
                console.error('Error booking appointment:', error);
                toast.error('Failed to book appointment. Please try again later.', {
                    position: 'top-right',
                    autoClose: 5000,
                });
            }
        }
    };

    return (
        <div className="appointment-page">
            <h1>Book an Appointment</h1>
            <Calendar
                localizer={localizer}
                events={events}
                selectable
                onSelectSlot={handleSelectSlot}
                defaultView="month"
                views={['month', 'week', 'day']}
                step={30}
                timeslots={2}
                style={{ height: 600 }}
                min={new Date(1970, 1, 1, 10)} // Start time 10:00 AM
                max={new Date(1970, 1, 1, 20)} // End time 8:00 PM
                eventPropGetter={(event) => ({
                    style: {
                        backgroundColor: event.title === 'Booked' ? '#d9534f' : '#5cb85c',
                    },
                })}
                onSelecting={(slotInfo) => {
                    if (slotInfo.start.getHours() < 10 || (slotInfo.start.getHours() >= 13 && slotInfo.start.getHours() < 15) || slotInfo.start.getHours() >= 20) {
                        return false;
                    }
                    return true;
                }}
            />
            {selectedDate && (
                <div className="timeslot-container">
                    <h2>Select a time slot on {moment(selectedDate).format('MMMM Do YYYY')}</h2>
                    <div className="timeslots">
                        {generateTimeSlots(selectedDate).map(({ time, isBooked }) => (
                            <button
                                key={time}
                                className={`timeslot ${isBooked ? 'booked' : ''} ${selectedTime === time ? 'selected' : ''}`}
                                onClick={() => !isBooked && handleTimeSlotClick(time)}
                                disabled={isBooked}
                            >
                                {moment(time).format('h:mm A')}
                            </button>
                        ))}
                    </div>
                </div>
            )}
            {formVisible && selectedTime && (
                <div className="appointment-form">
                    <h2>Enter your details for {moment(selectedTime).format('MMMM Do YYYY, h:mm A')}</h2>
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="name">Name:</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="email">Email:</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="contactNo">Contact No:</label>
                        <input
                            type="text"
                            id="contactNo"
                            name="contactNo"
                            value={formData.contactNo}
                            onChange={handleChange}
                            required
                        />

                        <button type="submit" className="btn-primary">Confirm Appointment</button>
                    </form>
                </div>
            )}
            <ToastContainer />
        </div>
    );
};

export default AppointmentPage;
