import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';
import './AppointmentPage.css'; // Import your CSS file for styling

const AppointmentPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        contactNo: '',
        date: '',
        slot: ''
    });

    const [availableSlots, setAvailableSlots] = useState([]);
    const [selectedDate, setSelectedDate] = useState(new Date());
    const [error, setError] = useState('');

    // Disable past dates
    const tileDisabled = ({ date }) => {
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        return date < today;
    };

    const handleDateChange = async (date) => {
        const selectedDate = date.toISOString().split('T')[0]; // Format date to YYYY-MM-DD
        setSelectedDate(date);
        setFormData({
            ...formData,
            date: selectedDate
        });

        try {
            const response = await axios.get(`http://localhost:5000/api/slots?date=${selectedDate}`);
            if (response.data.slots) {
                setAvailableSlots(response.data.slots);
                setError('');
            } else {
                setAvailableSlots([]);
                setError('No slots available for the selected date.');
            }
        } catch (error) {
            console.error('Error fetching slots:', error);
            setError('Failed to fetch available slots.');
        }
    };

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/api/appointments', formData);
            alert('Appointment booked successfully!');
            setFormData({
                name: '',
                email: '',
                contactNo: '',
                date: '',
                slot: ''
            });
            setAvailableSlots([]);
        } catch (error) {
            console.error('Error booking appointment:', error);
            alert('Failed to book appointment.');
        }
    };

    return (
        <div className="appointment-page">
            <h1>Book an Appointment</h1>
            <div className="calendar-container">
                <Calendar
                    onChange={handleDateChange}
                    value={new Date(formData.date)}
                    tileDisabled={tileDisabled}
                    minDate={new Date()}
                />
            </div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="contactNo">Contact Number:</label>
                <input
                    type="text"
                    id="contactNo"
                    name="contactNo"
                    value={formData.contactNo}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="slot">Select Time Slot:</label>
                <select
                    id="slot"
                    name="slot"
                    value={formData.slot}
                    onChange={handleChange}
                    required
                >
                    <option value="">Select a slot</option>
                    {availableSlots.length > 0 ? (
                        availableSlots.map((slot) => (
                            <option key={slot} value={slot}>{slot}</option>
                        ))
                    ) : (
                        <option value="">No available slots</option>
                    )}
                </select>

                {error && <p className="error">{error}</p>}

                <button type="submit">Book Appointment</button>
            </form>
        </div>
    );
};

export default AppointmentPage;
