import React, { useState } from 'react';
import './ContactUs.css'; // Import your CSS file for styling

const ContactUs = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        subject: '',
        message: '',
    });

    const [formStatus, setFormStatus] = useState('');

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        // Example of form submission logic
        // Replace this with actual form submission code
        setFormStatus('Thank you for your message. We will get back to you shortly.');
        setFormData({
            name: '',
            email: '',
            subject: '',
            message: '',
        });
    };

    return (
        <div className="contact-us">
            <section className="contact-intro">
                <div className="contact-intro-content">
                    <h1>Contact Us</h1>
                    <p>We'd love to hear from you! Whether you have questions about our services or need assistance, feel free to reach out.</p>
                </div>
            </section>
            <section className="contact-form">
                <div className="form-container">
                    <h2>Get in Touch</h2>
                    {formStatus && <p className="form-status">{formStatus}</p>}
                    <form onSubmit={handleSubmit}>
                        <label htmlFor="name">Name:</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="email">Email:</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="subject">Subject:</label>
                        <input
                            type="text"
                            id="subject"
                            name="subject"
                            value={formData.subject}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="message">Message:</label>
                        <textarea
                            id="message"
                            name="message"
                            value={formData.message}
                            onChange={handleChange}
                            required
                        ></textarea>

                        <button type="submit" className="btn-primary">Send Message</button>
                    </form>
                </div>
            </section>
            <section className="contact-details">
                <div className="details-container">
                    <h2>Our Contact Details</h2>
                    <p><i className="fas fa-phone"></i> Phone: +123 456 7890</p>
                    <p><i className="fas fa-envelope"></i> Email: contact@physioclinic.com</p>
                    <p><i className="fas fa-map-marker-alt"></i> Address: 123 Health Street, Wellness City, HC 45678</p>
                </div>
            </section>
        </div>
    );
}

export default ContactUs;
