import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import axios from 'axios';
import './AppointmentPage.css';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const localizer = momentLocalizer(moment);

const AppointmentPage = () => {
    const [events, setEvents] = useState([]);
    const [selectedSlot, setSelectedSlot] = useState(null);

    useEffect(() => {
        fetchAppointments();
    }, []);

    const fetchAppointments = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/appointments');
            const appointments = response.data.map(appointment => ({
                title: 'Booked',
                start: new Date(appointment.date),
                end: new Date(new Date(appointment.date).getTime() + 30 * 60000), // Assuming 30 minute slots
                allDay: false,
            }));
            setEvents(appointments);
        } catch (error) {
            console.error('Error fetching appointments:', error);
        }
    };

    const handleSelectSlot = ({ start, end }) => {
        const isSlotBooked = events.some(event =>
            (start >= event.start && start < event.end) ||
            (end > event.start && end <= event.end)
        );

        const isWithinTimeSlot = (
            (start.getHours() >= 10 && start.getHours() < 13) ||
            (start.getHours() >= 15 && start.getHours() < 20)
        );

        const isFutureDate = moment(start).isAfter(moment().endOf('day'));

        if (!isFutureDate) {
            toast.error('You can only book appointments from tomorrow onwards.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else if (!isWithinTimeSlot) {
            toast.error('Appointments can only be booked between 10 AM - 1 PM and 3 PM - 8 PM.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else if (isSlotBooked) {
            toast.error('This slot is already booked. Please choose another one.', {
                position: 'top-right',
                autoClose: 5000,
            });
        } else {
            setSelectedSlot({ start, end });
        }
    };

    const handleSubmit = async () => {
        if (!selectedSlot) return;

        const name = prompt("Enter your name:");
        const email = prompt("Enter your email:");

        if (name && email) {
            try {
                const response = await axios.post('http://localhost:5000/api/appointments', {
                    name,
                    email,
                    date: selectedSlot.start,
                });
                if (response.data) {
                    toast.success('Appointment booked successfully!', {
                        position: 'top-right',
                        autoClose: 5000,
                    });
                    fetchAppointments();
                    setSelectedSlot(null);
                }
            } catch (error) {
                console.error('Error booking appointment:', error);
                toast.error('Failed to book appointment. Please try again later.', {
                    position: 'top-right',
                    autoClose: 5000,
                });
            }
        }
    };

    return (
        <div className="appointment-page">
            <h1>Book an Appointment</h1>
            <Calendar
                localizer={localizer}
                events={events}
                selectable
                onSelectSlot={handleSelectSlot}
                defaultView="week"
                views={['month', 'week', 'day']}
                step={30}
                timeslots={2}
                style={{ height: 600 }}
                min={new Date(1970, 1, 1, 10)} // Start time 10:00 AM
                max={new Date(1970, 1, 1, 20)} // End time 8:00 PM
            />
            {selectedSlot && (
                <div className="appointment-details">
                    <h2>Selected Slot</h2>
                    <p>{`Start: ${selectedSlot.start.toLocaleString()}`}</p>
                    <p>{`End: ${selectedSlot.end.toLocaleString()}`}</p>
                    <button onClick={handleSubmit} className="btn-primary">Confirm Appointment</button>
                </div>
            )}
            <ToastContainer />
        </div>
    );
};

export default AppointmentPage;
