import React, { useState } from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './AppointmentPage.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AppointmentPage = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        contact_no: '',
        age: '',
        condition: '',
        therapist_gender: '',
        address: '',
        date: null,
        slot: ''
    });
    const [error, setError] = useState('');

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleDateChange = (date) => {
        setFormData({
            ...formData,
            date: date
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const localDate = new Date(formData.date.getTime() - formData.date.getTimezoneOffset() * 60000).toISOString().split('T')[0];
            const response = await axios.post('http://localhost:5000/api/appointments', {
                ...formData,
                date: localDate // Adjusted date
            });
            toast.success('Appointment booked successfully!', {
                position: 'top-right',
                autoClose: 5000,
            });
            setFormData({
                name: '',
                email: '',
                contact_no: '',
                age: '',
                condition: '',
                therapist_gender: '',
                address: '',
                date: null,
                slot: ''
            });
        } catch (error) {
            console.error('Error booking appointment:', error.response ? error.response.data : error.message);
            toast.error('Failed to book appointment.', {
                position: 'top-right',
                autoClose: 5000,
            });
        }
    };

    return (
        <div className="appointment-page">
            <h1>Book an Appointment</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Name:</label>
                <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="email">Email:</label>
                <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="contact_no">Contact Number:</label>
                <input
                    type="text"
                    id="contact_no"
                    name="contact_no"
                    value={formData.contact_no}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="age">Age:</label>
                <input
                    type="number"
                    id="age"
                    name="age"
                    value={formData.age}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="condition">Condition/Problem (optional):</label>
                <textarea
                    id="condition"
                    name="condition"
                    value={formData.condition}
                    onChange={handleChange}
                />

                <label htmlFor="therapist_gender">Gender of Therapist (optional):</label>
                <select
                    id="therapist_gender"
                    name="therapist_gender"
                    value={formData.therapist_gender}
                    onChange={handleChange}
                >
                    <option value="">Select a gender</option>
                    <option value="male">Male</option>
                    <option value="female">Female</option>
                    <option value="any">Any</option>
                </select>

                <label htmlFor="address">Address/Location:</label>
                <textarea
                    id="address"
                    name="address"
                    value={formData.address}
                    onChange={handleChange}
                    required
                />

                <label htmlFor="date">Select Date:</label>
                <DatePicker
                    selected={formData.date}
                    onChange={handleDateChange}
                    minDate={new Date()}
                    dateFormat="yyyy-MM-dd"
                    required
                />

                <label htmlFor="slot">Select Time Slot:</label>
                <select
                    id="slot"
                    name="slot"
                    value={formData.slot}
                    onChange={handleChange}
                    required
                >
                    <option value="">Select a slot</option>
                    <option value="10:00:00">10:00:00</option>
                    <option value="11:00:00">11:00:00</option>
                    <option value="12:00:00">12:00:00</option>
                    <option value="15:00:00">15:00:00</option>
                    <option value="16:00:00">16:00:00</option>
                    <option value="17:00:00">17:00:00</option>
                    <option value="18:00:00">18:00:00</option>
                    <option value="19:00:00">19:00:00</option>
                </select>

                {error && <p className="error">{error}</p>}

                <button type="submit">Book Appointment</button>
            </form>
        </div>
    );
};

export default AppointmentPage;
